export type ActivityType = 'attraction' | 'reservation' | 'transportation' | 'accommodation' | 'other';

export interface Activity {
  id: string;
  type: ActivityType;
  title: string;
  description?: string;
  startTime: string;
  endTime?: string;
  location?: string;
  address?: string;
  confirmationNumber?: string;
  cost?: number;
  currency?: string;
  notes?: string;
  url?: string;
  phone?: string;
}

export interface Day {
  id: string;
  date: string;
  title?: string;
  activities: Activity[];
}

export interface Itinerary {
  id: string;
  title: string;
  destination: string;
  startDate: string;
  endDate: string;
  days: Day[];
  totalBudget?: number;
  currency?: string;
  travelers?: number;
  notes?: string;
}

import { Activity } from "@/types/itinerary";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { formatTime, formatCurrency } from "@/lib/utils";
import {
  MapPin,
  Clock,
  DollarSign,
  Phone,
  ExternalLink,
  Edit,
  Trash2,
  Train,
  Hotel,
  UtensilsCrossed,
  Landmark
} from "lucide-react";

interface ActivityCardProps {
  activity: Activity;
  onEdit?: () => void;
  onDelete?: () => void;
}

const activityIcons = {
  attraction: Landmark,
  reservation: UtensilsCrossed,
  transportation: Train,
  accommodation: Hotel,
  other: MapPin,
};

const activityColors = {
  attraction: "default",
  reservation: "secondary",
  transportation: "warning",
  accommodation: "success",
  other: "outline",
} as const;

export function ActivityCard({ activity, onEdit, onDelete }: ActivityCardProps) {
  const Icon = activityIcons[activity.type];

  return (
    <Card className="hover:shadow-md transition-shadow">
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between gap-4">
          <div className="flex items-start gap-3 flex-1">
            <div className="p-2 rounded-lg bg-muted">
              <Icon className="h-5 w-5 text-muted-foreground" />
            </div>
            <div className="flex-1 min-w-0">
              <div className="flex items-center gap-2 mb-1">
                <CardTitle className="text-lg">{activity.title}</CardTitle>
                <Badge variant={activityColors[activity.type]}>
                  {activity.type}
                </Badge>
              </div>
              {activity.description && (
                <p className="text-sm text-muted-foreground">{activity.description}</p>
              )}
            </div>
          </div>
          <div className="flex gap-1">
            {onEdit && (
              <Button variant="ghost" size="icon" onClick={onEdit}>
                <Edit className="h-4 w-4" />
              </Button>
            )}
            {onDelete && (
              <Button variant="ghost" size="icon" onClick={onDelete}>
                <Trash2 className="h-4 w-4 text-destructive" />
              </Button>
            )}
          </div>
        </div>
      </CardHeader>
      <CardContent className="space-y-2">
        <div className="flex items-center gap-2 text-sm">
          <Clock className="h-4 w-4 text-muted-foreground" />
          <span className="font-medium">{formatTime(activity.startTime)}</span>
          {activity.endTime && (
            <span className="text-muted-foreground">- {formatTime(activity.endTime)}</span>
          )}
        </div>

        {activity.location && (
          <div className="flex items-start gap-2 text-sm">
            <MapPin className="h-4 w-4 text-muted-foreground mt-0.5" />
            <div>
              <div>{activity.location}</div>
              {activity.address && (
                <div className="text-muted-foreground text-xs">{activity.address}</div>
              )}
            </div>
          </div>
        )}

        {activity.cost && (
          <div className="flex items-center gap-2 text-sm">
            <DollarSign className="h-4 w-4 text-muted-foreground" />
            <span>{formatCurrency(activity.cost, activity.currency)}</span>
          </div>
        )}

        {activity.confirmationNumber && (
          <div className="text-sm">
            <span className="text-muted-foreground">Confirmation: </span>
            <span className="font-mono font-medium">{activity.confirmationNumber}</span>
          </div>
        )}

        {activity.phone && (
          <div className="flex items-center gap-2 text-sm">
            <Phone className="h-4 w-4 text-muted-foreground" />
            <a href={`tel:${activity.phone}`} className="text-primary hover:underline">
              {activity.phone}
            </a>
          </div>
        )}

        {activity.url && (
          <div className="flex items-center gap-2 text-sm">
            <ExternalLink className="h-4 w-4 text-muted-foreground" />
            <a
              href={activity.url}
              target="_blank"
              rel="noopener noreferrer"
              className="text-primary hover:underline"
            >
              View details
            </a>
          </div>
        )}

        {activity.notes && (
          <div className="text-sm text-muted-foreground pt-2 border-t border-border">
            {activity.notes}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import { Day } from "@/types/itinerary";
import { ActivityCard } from "./ActivityCard";
import { Button } from "@/components/ui/button";
import { formatDate } from "@/lib/utils";
import { Plus } from "lucide-react";

interface DaySectionProps {
  day: Day;
  dayNumber: number;
  onAddActivity: () => void;
  onEditActivity: (activityId: string) => void;
  onDeleteActivity: (activityId: string) => void;
}

export function DaySection({
  day,
  dayNumber,
  onAddActivity,
  onEditActivity,
  onDeleteActivity
}: DaySectionProps) {
  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div>
          <div className="flex items-baseline gap-3">
            <h2 className="text-2xl font-bold text-foreground">Day {dayNumber}</h2>
            {day.title && (
              <span className="text-lg text-muted-foreground">{day.title}</span>
            )}
          </div>
          <p className="text-sm text-muted-foreground mt-1">{formatDate(day.date)}</p>
        </div>
        <Button onClick={onAddActivity} size="sm">
          <Plus className="h-4 w-4" />
          Add Activity
        </Button>
      </div>

      <div className="space-y-3">
        {day.activities.length === 0 ? (
          <div className="text-center py-12 bg-muted/30 rounded-lg border-2 border-dashed border-border">
            <p className="text-muted-foreground">No activities planned for this day</p>
            <Button onClick={onAddActivity} variant="outline" size="sm" className="mt-4">
              <Plus className="h-4 w-4" />
              Add First Activity
            </Button>
          </div>
        ) : (
          day.activities
            .sort((a, b) => a.startTime.localeCompare(b.startTime))
            .map((activity) => (
              <ActivityCard
                key={activity.id}
                activity={activity}
                onEdit={() => onEditActivity(activity.id)}
                onDelete={() => onDeleteActivity(activity.id)}
              />
            ))
        )}
      </div>
    </div>
  );
}

import { useState } from "react";
import { Itinerary } from "@/types/itinerary";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { DialogFooter } from "@/components/ui/dialog";

interface ItineraryFormProps {
  itinerary?: Itinerary;
  onSubmit: (data: Omit<Itinerary, 'id' | 'days'>) => void;
  onCancel: () => void;
}

export function ItineraryForm({ itinerary, onSubmit, onCancel }: ItineraryFormProps) {
  const [formData, setFormData] = useState({
    title: itinerary?.title || '',
    destination: itinerary?.destination || '',
    startDate: itinerary?.startDate || '',
    endDate: itinerary?.endDate || '',
    totalBudget: itinerary?.totalBudget?.toString() || '',
    currency: itinerary?.currency || 'USD',
    travelers: itinerary?.travelers?.toString() || '1',
    notes: itinerary?.notes || '',
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit({
      title: formData.title,
      destination: formData.destination,
      startDate: formData.startDate,
      endDate: formData.endDate,
      totalBudget: formData.totalBudget ? parseFloat(formData.totalBudget) : undefined,
      currency: formData.currency || undefined,
      travelers: formData.travelers ? parseInt(formData.travelers) : undefined,
      notes: formData.notes || undefined,
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="space-y-2">
        <Label htmlFor="title">Trip Title *</Label>
        <Input
          id="title"
          value={formData.title}
          onChange={(e) => setFormData({ ...formData, title: e.target.value })}
          placeholder="e.g., Summer Europe Trip"
          required
        />
      </div>

      <div className="space-y-2">
        <Label htmlFor="destination">Destination *</Label>
        <Input
          id="destination"
          value={formData.destination}
          onChange={(e) => setFormData({ ...formData, destination: e.target.value })}
          placeholder="e.g., Paris, France"
          required
        />
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div className="space-y-2">
          <Label htmlFor="startDate">Start Date *</Label>
          <Input
            id="startDate"
            type="date"
            value={formData.startDate}
            onChange={(e) => setFormData({ ...formData, startDate: e.target.value })}
            required
          />
        </div>

        <div className="space-y-2">
          <Label htmlFor="endDate">End Date *</Label>
          <Input
            id="endDate"
            type="date"
            value={formData.endDate}
            onChange={(e) => setFormData({ ...formData, endDate: e.target.value })}
            min={formData.startDate}
            required
          />
        </div>
      </div>

      <div className="grid grid-cols-2 gap-4">
        <div className="space-y-2">
          <Label htmlFor="totalBudget">Total Budget</Label>
          <Input
            id="totalBudget"
            type="number"
            step="0.01"
            value={formData.totalBudget}
            onChange={(e) => setFormData({ ...formData, totalBudget: e.target.value })}
            placeholder="0.00"
          />
        </div>

        <div className="space-y-2">
          <Label htmlFor="travelers">Number of Travelers</Label>
          <Input
            id="travelers"
            type="number"
            min="1"
            value={formData.travelers}
            onChange={(e) => setFormData({ ...formData, travelers: e.target.value })}
          />
        </div>
      </div>

      <div className="space-y-2">
        <Label htmlFor="notes">Trip Notes</Label>
        <Textarea
          id="notes"
          value={formData.notes}
          onChange={(e) => setFormData({ ...formData, notes: e.target.value })}
          rows={3}
          placeholder="Any additional information about your trip..."
        />
      </div>

      <DialogFooter>
        <Button type="button" variant="outline" onClick={onCancel}>
          Cancel
        </Button>
        <Button type="submit">
          {itinerary ? 'Update' : 'Create'} Itinerary
        </Button>
      </DialogFooter>
    </form>
  );
}

